import React, { useState, useEffect } from 'react';
import './App.css';
import TextEditor from './components/TextEditor';
import Header from './components/Header';
import About from './components/About'; // Importa el componente About

import { API_SERVER_URL } from './components/Url';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

function App() {
  const [keywordsList, setKeywordsList] = useState([]);

  useEffect(() => {
    fetch(`${API_SERVER_URL}/keywords`)
      .then((response) => response.json())
      .then((data) => setKeywordsList(data.keywords))
      .catch((error) => console.error('Error fetching keywords:', error));
  }, []);

  return (
    <Router>
  <div className="App">
    <Header />
    <Switch>
      <Route path="/" exact>
        <TextEditor keywordsList={keywordsList} />
      </Route>
      <Route path="/about">
        <About />
      </Route>
    </Switch>
  </div>
</Router>

  );
}

export default App;
